import streamlit as st
import random
import time

# Set the page layout
st.set_page_config(page_title="My Love for You", layout="wide")

# Title and message
st.title("💖 A Love Letter Just For You 💖")
st.header("My Love for You, Vani")

# Sweet message
st.write("""
    My dearest Baby, 💌
    There are no words in the world that can truly express how much I love you. 
    Every moment with you feels like magic, and every day I am grateful for your love.
    I'm here to remind you that no matter what, you'll always have my heart. 💖
""")

# Add some space
st.markdown("<br>", unsafe_allow_html=True)

# Add a playful message and "No move" button
st.subheader("Please don't move away from me! 😊")

# Button that "moves" the message and brings flowers on the page
if st.button("Don't Move!"):
    # After clicking the button, display a message and bring flowers
    st.write("Yay! You clicked the 'Don't Move!' button! 🎉 Here's some love from me to you...")

    # Show a random flower image every time the button is clicked
    flowers = [
        "data:image/jpeg;base64,",  # Animated flower GIF
        "data:image/jpeg;base64,",  # Flower icon
        "data:image/jpeg;base64,",  # Another flower icon
    ]
    flower = random.choice(flowers)

    # Show the flower image on the page
    st.image(flower, width=200)

    # Additional love message after the button click
    st.write("""
        You make my life complete, and with every little moment we share, 
        I fall in love with you even more. Here's to many more beautiful moments together! 🌸
    """)

# If the button is not clicked, show the following
else:
    st.write("Click the button below to see some love coming your way! 💐")
    # Add some predefined flowers as decoration before clicking the button
    flower1 = "data:image/jpeg;base64,"
    flower2 = "data:image/jpeg;base64,"

    # Display flowers before button click
    st.image(flower1, width=100)
    st.image(flower2, width=100)

# Footer with a personal message
st.markdown("""
    <hr>
    <h4 style="text-align:center;">With all my love, Arjun 💖</h4>
""", unsafe_allow_html=True)
